INSERT INTO `iddpm_virtuemart_products_en_gb` (`virtuemart_product_id`, `product_s_desc`, `product_desc`, `product_name`, `metadesc`, `metakey`, `customtitle`, `slug`) VALUES
(1, '', '<h2><strong>Thông tin sản phẩm</strong></h2>\r\n<p>Chất liệu lụa mềm mại, mịn màng cùng với kiểu dáng cổ đỗ độc đáo tạo nên sự khác biệt và thu hút mọi ánh nhìn.</p>\r\n<p>Áo sơ mi lụa cổ đỗ có thể dễ dàng phối hợp với nhiều trang phục khác nhau, tạo nên nhiều phong cách thời trang đa dạng. Bạn có thể mặc áo sơ mi lụa cổ đỗ với quần tây, <a href=\"https://hoyang.vn/chan-vay-but-chi-dang-dai/\">chân váy bút chì chẻ tà VHY29</a>, hoặc quần short để đi làm, đi chơi hoặc tham dự các sự kiện quan trọng.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2024/04/2.jpg\" alt=\"chân váy bút chì chẻ trước\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2024/04/4.jpg\" alt=\"chân váy bút chi công sở\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2024/04/5.jpg\" alt=\"chân váy bút chi công sở phối nơ\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2024/04/3.jpg\" alt=\"chân váy bút chì chẻ trước thanh lịch\"></p>', 'Áo Sơ Mi Lụa Tay Dài Cổ Đỗ Sang Trọng', '', '', '', 'áo-sơ-mi-lụa-tay-dài-cổ-đỗ-sang-trọng'),
(2, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo sơ mi cổ bèo thanh lịch là một lựa chọn hoàn hảo cho những ai yêu thích phong cách nữ tính, tinh tế nhưng vẫn đầy sự trang nhã và lịch sự. Với thiết kế cổ bèo nhẹ nhàng, chiếc áo này không chỉ mang lại cảm giác thoải mái mà còn tôn lên vẻ đẹp duyên dáng của người mặc.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2024/11/fa47f109a7d11c8f45c02.jpg\" alt=\"fa47f109a7d11c8f45c02\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2024/11/d97acf2199f922a77be81.jpg\" alt=\"d97acf2199f922a77be81\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2024/11/cb874db51b6da033f97c3.jpg\" alt=\"cb874db51b6da033f97c3\"></p>\r\n<p> </p>', 'Áo sơ mi cổ bèo thanh lịch', '', '', '', 'áo-sơ-mi-cổ-bèo-thanh-lịch'),
(3, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo được thiết kế theo kiểu dáng gợi cảm, cổ tim, phối 2 màu đắp chéo tạo điểm nhấn mạnh về thị giác, mang đến sự hiện đại và trẻ trung cho người mặc.</p>\r\n<p>Nàng có thể phối chiếc áo này cùng với <a href=\"https://hoyang.vn/chan-vay-xe-truoc/\">chân váy chẻ trước V46</a> để tạo nên một outfit thời trang “ngọt lịm” của quý cô sành điệu.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-kieu-phoi-2-mau.jpg\" alt=\"chân váy công sở\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-so-mi-kieu.jpg\" alt=\"chân váy bút chì thiết kế\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-so-mi-ca-tinh.jpg\" alt=\"chân váy công sở chẻ trước\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2022/11/ao-so-mi-cong-so.jpg\" alt=\"chân váy bút chì\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2024/04/ao-kieu-2-mau-ao-so-mi-1.jpg\" alt=\"áo kiểu sơ mi 2 màu cài nút tay dài\"></p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://hoyang.vn/wp-content/uploads/2024/04/ao-kieu-2-mau-den-do.jpg\" alt=\"áo sơ mi kiểu thanh lịch\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/ao-kieu-2-mau.jpg\" alt=\"áo kiểu sơ mi 2 màu cài nút\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/ao-kieu-2-mau-phoi-chan-vay-but-chi.jpg\" alt=\"áo sơ mi kiểu thanh lịch đen đỏ\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi 2 Màu Đắp Chéo', '', '', '', 'áo-sơ-mi-2-màu-đắp-chéo'),
(4, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo sơ mi phối màu tương phản giúp bạn thể hiện cá tính riêng của mình một cách độc đáo và ấn tượng, thu hút mọi ánh nhìn mỗi khi xuất hiện.</p>\r\n<p>Nàng có thể phối áo cùng <a href=\"https://hoyang.vn/chan-vay-but-chi-cong-so-xanh-duong/\">chân váy bút chì ôm body VHY30</a> hoặc <a href=\"https://hoyang.vn/chan-vay-xe-truoc/\">chân váy bút chì dáng dài V46</a> như mẫu đang mặc để có 1 outfit thời trang ấn tượng.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-ca-tinh.jpg\" alt=\"chân váy ngắn bút chì phối nút\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-cong-so.jpg\" alt=\"chân váy ngắn bút chì phối nút công sở\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-cong-so-2-mau.jpg\" alt=\"chân váy công sở\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/1.jpg\" alt=\"1\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/3.jpg\" alt=\"3\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Phối Màu Tương Phản Độc Đáo', '', '', '', 'áo-sơ-mi-phối-màu-tương-phản-độc-đáo'),
(5, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo sơ mi được thiết kế với điểm nhấn phần cổ áo – cổ tim xếp bèo điệu đà. Họa tiết in trên áo mới lạ, giúp bạn thể hiện phong cách cá tính của riêng mình.</p>\r\n<p>Nếu nàng yêu mến phong cách trẻ trung, năng động thì hãy phối áo cùng với <a href=\"https://hoyang.vn/quan-vay-ngan-2-ta/\">skirt ngắn phối nút VHY31</a>, thích phong cách sang chảng thì phối áo cùng <a href=\"https://hoyang.vn/quan-ong-rong-phi-lua/\">quần lụa quốc dân QHY17</a> đang làm mưa làm gió tại NhiNhiShop nhé.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/2-1.jpg\" alt=\"áo lụa họa tiết sang trọng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/5-1.jpg\" alt=\"áo lụa tay dài cổ bèo\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/7.jpg\" alt=\"áo lụa tay dài cổ bèo quý phái sang chảnh\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2021/06/ao-so-mi-co-beo.jpg\" alt=\"áo sơ mi cổ bèo\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2021/06/ao-so-mi-hoa-tiet.jpg\" alt=\"áo sơ mi họa tiết\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2021/06/ao-si-mu-tay-dai.jpg\" alt=\"áo sơ mi họa tiết tay dài\"></p>', 'Áo Sơ Mi Cổ Tim Xếp Bèo In Họa Tiết', '', '', '', 'áo-sơ-mi-cổ-tim-xếp-bèo-in-họa-tiết'),
(6, '', '<h2>Thông tin sản phẩm</h2>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2021/03/anhbia.png\" alt=\"áo sơ mi công sở\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2021/03/ao-so-mi-trang.jpg\" alt=\"áo sơ mi trắng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2021/03/ao-so-mi-cong-so.jpg\" alt=\"áo sơ mi công sở thanh lịch\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2021/03/ao-so-mi-cong-so-thanh-lich.jpg\" alt=\"áo sơ mi công sở thời thượng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2021/03/ao-so-mi-tay-ngan.jpg\" alt=\"áo sơ mi tím tay ngắn\"></p>\r\n<p style=\"text-align: center;\"><strong><img src=\"https://hoyang.vn/wp-content/uploads/2021/03/ao-so-mi-mau-tim.jpg\" alt=\"áo sơ mi tím\"><img src=\"https://hoyang.vn/wp-content/uploads/2021/03/ao-so-mi-thiet-ke.jpg\" alt=\"áo sơ mi thiết kế\"></strong></p>\r\n<p style=\"text-align: center;\"><strong><img src=\"https://hoyang.vn/wp-content/uploads/2021/03/set-so-mi-ca-tinh.jpg\" alt=\"áo sơ mi cá tính\"></strong></p>\r\n<p style=\"text-align: center;\"><strong><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/chan-vay-caro-den.jpg\" alt=\"áo sơ mi nữ\"></strong></p>\r\n<p style=\"text-align: center;\"><strong><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/chan-vay-caro-den-chu-a.jpg\" alt=\"áo sơ mi nữ tay ngắn nano\"></strong></p>\r\n<p style=\"text-align: center;\"><strong><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/chan-vay-caro-den-phoi-ao-so-mi.jpg\" alt=\"áo sơ mi nữ tay ngắn nano sang xịn\"></strong></p>\r\n<p style=\"text-align: center;\"><strong><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/chan-vay-caro-den-ao-den.jpg\" alt=\"áo sơ mi nữ tay ngắn\"></strong></p>', 'Áo Sơ Mi Công Sở Cộc Tay', '', '', '', 'áo-sơ-mi-công-sở-cộc-tay'),
(7, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Kết hợp những yếu tố tưởng chừng đối lập – lụa mềm mại, lưới táo bạo, cổ bèo thanh tao, tay lưới bí ẩn, màu đen quyền lực, áo lụa HoYang như một vũ điệu đầy mê hoặc, đưa người phụ nữ đến với đỉnh cao của sự quyến rũ.</p>\r\n<p>Diện chiếc áo này, bạn sẽ trở thành tâm điểm của mọi ánh nhìn, thu hút mọi sự chú ý và khẳng định đẳng cấp thời trang của bản thân. Hãy tự tin tỏa sáng, để thế giới say đắm trước vẻ đẹp bí ẩn và đầy thu hút của bạn!</p>\r\n<p>Trong ảnh, mẫu phối áo cùng <a href=\"https://hoyang.vn/chan-vay-dai-vintage-cap-cao/\">chân váy VHY24</a>.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/anhbia.png\" alt=\"chân váy lụa dài\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/chan-vay-lua-ao-co-beo.jpg\" alt=\"chân váy lụa dài thanh lịch\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/chan-vay-lua-do.jpg\" alt=\"chân váy lụa đỏ\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/chan-vay-lua-saphia.jpg\" alt=\"chân váy lụa saphia\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/04/chan-vay-lua-dai.jpg\" alt=\"chân váy lụa dài thanh lịch sang trọng\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Lụa Cổ Bèo Tay Lưới Sang Chảnh', '', '', '', 'áo-lụa-cổ-bèo-tay-lưới-sang-chảnh'),
(8, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Một trong những dấu ấn của thời gian mang lại nét đẹp thanh lịch, sang chảnh đến từ những điều tối giản mà không hề đơn điệu từ chiếc áo sơ mi lụa tơ bèo cao cấp tại Hoyang.</p>\r\n<p>Với thiết kế đơn giản dựa trên nền vải lụa mềm, mịn cực mát phối bèo nhún ở phần cổ và tay là style ưa chuộng của các cô nàng nữ tính dịu dàng. Mẫu áo bèo nhún cách mix phổ biến là phối cùng <a href=\"https://hoyang.vn/quan-ong-rong-culottes-hack-dang/\">quần lụa ống suông xanh nhạt QUAN001</a> hoặc <a href=\"https://hoyang.vn/quan-lua-ong-suong-mem-mai/\">cá tính hơn khi mix cùng quần lụa xanh lá QHY74</a> vô cùng duyên dáng cá tính và khác biệt khi xuất hiện ở bất cứ nơi nào.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-so-mi-lua-trang.jpg\" alt=\"áo sơ mi lụa trắng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-so-mi-lua-trang-tay-beo.jpg\" alt=\"áo sơ mi lụa trắng tay bèo\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-so-mi-lua-trang-phoi-vien.jpg\" alt=\"áo sơ mi lụa trắng cổ tim phối bèo\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-so-mi-lua-trang-co-tim.jpg\" alt=\"áo sơ mi lụa trắng cổ tim\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-lua-1.jpg\" alt=\"áo sơ mi lụa\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-lua-xep-li.jpg\" alt=\"áo sơ mi lụa xếp li\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-lua-tay-beo.jpg\" alt=\"áo sơ mi lụa cổ tim tay bèo\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-lua-co-tim.jpg\" alt=\"áo sơ mi lụa cổ tim\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Lụa Tay Bèo Thanh Lịch', '', '', '', 'áo-lụa-tay-bèo-thanh-lịch'),
(9, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo lụa in họa tiết là mẫu áo đang được quý cô tại HOYANG cực kỳ yêu mến bởi nét độc đáo đến từ họa tiết độc đáo trên nền lụa satin mềm mịn, thoáng mát.</p>\r\n<p>Thiết kế áo form cổ tim, đính nút sang chảnh kết hợp cùng tay lỡ nhẹ nhàng giúp quý cô tôn lên nét thanh lịch và sang trọng. Trong ảnh, mẫu phối áo cùng <a href=\"https://hoyang.vn/quan-lua-ong-rong/\">quần lụa ống rộng đen Q42</a> và <a href=\"https://hoyang.vn/quan-ong-rong-xe-ta/\">quần tây ống rộng phối lưới QHY59</a>.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-co-tim.jpg\" alt=\"áo lụa cổ tim\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-hoa-tiet.jpg\" alt=\"áo lụa họa tiết\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-tay-lo.jpg\" alt=\"áo lụa tay lỡ\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-hoa-tiet-do-den.jpg\" alt=\"áo lụa họa tiết đỏ đen\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-form-suong.jpg\" alt=\"áo lụa form suông\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-xinh-xan.jpg\" alt=\"áo lụa xinh xắn\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-hoa-tiet-san-ho.jpg\" alt=\"áo lụa họa tiết san hô\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-hoa-tiet-co-tim-tay-lo.jpg\" alt=\"áo lụa họa tiết cổ tim tay lỡ\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Lụa Cổ Tim In Họa Tiết Độc Lạ', '', '', '', 'áo-lụa-cổ-tim-in-họa-tiết-độc-lạ'),
(10, '', '<h2>Thông tin sản phẩm</h2>\r\n<p dir=\"auto\">Với thiết kế form basic, màu sáng tươi sáng, item này mang đến cho người mặc vẻ ngoài trẻ trung, tươi mới nhưng điểm thanh lịch thì vẫn cao ngất. Do đó, dù bạn diện áo đi làm hay dạo phố thì vẫn đẹp khỏi nghĩ, là item cơ bản nhưng vô cùng thời thượng mà mọi cô nàng công sở đều cần có trong tủ đồ.</p>\r\n<p dir=\"auto\">Bạn có thể phối áo cùng <a href=\"https://hoyang.vn/quan-lua-ong-rong/\">quần ống rộng lưng thun Q42</a> hoặc <a href=\"https://hoyang.vn/chan-vay-dap-ta/\">chân váy body VHY2</a>8 như mẫu đang mặc, tuy quen thuộc nhưng không bao giờ nhàm chán, tính ứng dụng rất cao.</p>\r\n<p dir=\"auto\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-lua-hoa-xanh.jpg\" alt=\"áo lụa hoa xanh\"></p>\r\n<p dir=\"auto\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-so-mi-lua-hoa-xanh.jpg\" alt=\"áo sơ mi họa tiết xanh\"></p>\r\n<p dir=\"auto\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-so-mi-hoa-tiet.jpg\" alt=\"áo sơ mi lụa họa tiết\"></p>\r\n<p dir=\"auto\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-lua-tay-ngan-1.jpg\" alt=\"áo lụa tay ngắn\"></p>\r\n<p dir=\"auto\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-so-mi-nu.jpg\" alt=\"áo sơ mi nữ\"></p>\r\n<p dir=\"auto\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-so-mi.jpg\" alt=\"áo lụa sơ mi\"></p>\r\n<p dir=\"auto\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-hoa-do.jpg\" alt=\"áo lụa hoa đỏ\"></p>\r\n<p dir=\"auto\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-in-hoa.jpg\" alt=\"áo lụa in hoa\"></p>\r\n<p dir=\"auto\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-lua-so-mi-ngan-tay.jpg\" alt=\"áo lụa sơ mi ngắn tay\"></p>\r\n<p dir=\"auto\" style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Lụa Tay Ngắn In Hoa Ngọt Ngào', '', '', '', 'áo-sơ-mi-lụa-tay-ngắn-in-hoa-ngọt-ngào'),
(11, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Thiết kế form áo vạt trước ngắn, vạt sau dài, phối túi trước tạo cảm giác thoải mái tự tin năng động. Thiết kế tưởng chừng đơn giản nhưng mang lại phong cách riêng một cách tinh tế nhất không bao giờ lỗi mốt.</p>\r\n<p>Áo sử dụng chất liệu vải linen xước cao cấp, thoải mái khi diện áo đi làm, đi chơi đều trông thật đáng yêu và xinh xắn. Quý cô có thể phối áo cùng <a href=\"https://hoyang.vn/quan-short-dinh-nut-ca-tinh/\">quần short phối nút QS24</a> hoặc <a href=\"https://hoyang.vn/chan-vay-chu-a-tre-trung/\">chân váy ngắn VHY21</a>.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-croptop-ta-cheo.jpg\" alt=\"áo sơ mi croptop\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-croptop-dinh-nut.jpg\" alt=\"áo sơ mi croptop đính nút\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-croptop-trang.jpg\" alt=\"áo sơ mi croptop trắng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-croptop.jpg\" alt=\"áo sơ mi croptop\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-croptop-tay-ngan.jpg\" alt=\"áo sơ mi croptop tay ngắn\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-croptop-1.jpg\" alt=\"áo sơ mi croptop\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-croptop-vang.jpg\" alt=\"áo sơ mi croptop vàng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-croptop-tui-hop.jpg\" alt=\"áo sơ mi croptop tay ngắn túi trước\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Croptop Basic Có Túi', '', '', '', 'áo-sơ-mi-croptop-basic-có-túi'),
(12, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo sơ mi với tông màu sáng vẫn chưa có dấu hiệu hạ nhiệt. Kiểu áo này vẫn là sự lựa chọn hàng đầu của các cô nàng năng động và cá tính. Bên cạnh những cách mix đồ thông thường, áo sơ mi sáng màu còn là item không thể thiếu để giúp outfit đi biển của hội chị em thêm sang xịn.</p>\r\n<p>Trong ảnh, bạn mẫu phối áo cùng quần tây ống suông AHY , <a href=\"https://hoyang.vn/quan-lua-ong-suong-mem-mai/\">quần lụa suông dài QHY74</a> và <a href=\"https://hoyang.vn/ao-2-day-goi-cam/\">áo 2 dây AHY88</a>. Nếu bạn mua cùng lúc <a href=\"https://hoyang.vn/set-ao-so-mi-phoi-quan-lua/\">set đồ 3 món SBAHY74</a> sẽ được giảm 10%.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-lua-ca-tinh.jpg\" alt=\"áo sơ mi lụa cá tính\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-lua-sieu-xinh.jpg\" alt=\"áo sơ mi lụa siêu xinh\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-lua-xanh-la.jpg\" alt=\"áo sơ mi lụa xanh lá\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-so-mi-lua.jpg\" alt=\"áo sơ mi lụa\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/quan-lua-dai-lung-cao-ong-suong.jpg\" alt=\"quần lụa dài lưng cao ống suông\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/5.jpg\" alt=\"áo sơ mi xanh lá\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/6.jpg\" alt=\"áo sơ mi xanh lá tay dài\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/1-1.jpg\" alt=\"áo sơ mi lụa\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/4.jpg\" alt=\"áo sơ mi lụa tay dài thanh lịch điệu đà\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/3-1.jpg\" alt=\"áo sơ mi lụa tay dài thanh lịch\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/2-1.jpg\" alt=\"áo sơ mi lụa tay dài\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Lụa Tay Dài Cao Cấp', '', '', '', 'áo-sơ-mi-lụa-tay-dài-cao-cấp'),
(13, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Nhắc đến áo sơ mi chắc hẳn bạn sẽ liên tưởng đến những kiểu dáng truyền thống, form dài, có thể đóng thùng với quần hay chân váy. Nhưng với phong cách thời trang không ngừng biến đổi, áo sơ mi đã được cách tân hơn trở thành sơ mi croptop.</p>\r\n<p>Với phần trên cổ và tay áo vẫn được giữ nguyên, chiều dài áo được thu ngắn lại chỉ ngang rốn, khi kết hợp cùng với <a href=\"https://hoyang.vn/ao-blazer-tron/\">áo blazer ngắn tay AV19</a> và <a href=\"https://hoyang.vn/quan-short-kaki/\">quần short QS23</a> sẽ giúp nàng trông vừa trưởng thành vừa trẻ trung, năng động.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-croptop-tay-ngan-ke-soc.jpg\" alt=\"áo sơ mi croptop tay ngắn kẻ sọc\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-so-mi-croptop-tay-ngan-tui-hop.jpg\" alt=\"áo sơ mi croptop tay ngắn\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-so-mi-croptop-tui-hop-ke-soc.jpg\" alt=\"áo sơ mi croptop túi hộp\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-so-mi-ke-soc-tay-ngan.jpg\" alt=\"áo sơ mi kẻ sọc tay ngắn\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-so-mi-croptop.jpg\" alt=\"áo sơ mi croptop\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Croptop Tay Ngắn Kẻ Sọc', '', '', '', 'áo-sơ-mi-croptop-tay-ngắn-kẻ-sọc'),
(14, '', '<h2>Thông tin sản phẩm</h2>\r\n<p dir=\"ltr\">Nếu theo đuổi phong cách Vintage, cô nàng tiểu thư không thể bỏ lỡ mẫu áo sơ mi chấm bi tay bèo trong tủ đồ cá nhân. Phần cổ tròn nhún bèo và vai áo chấm bi mang lại sự dễ thương, xinh xắn.</p>\r\n<p dir=\"ltr\">Mix áo cùng <a href=\"https://hoyang.vn/chan-vay-xe-ta-sanh-dieu/\">chân váy nhún tà VHY07</a> hoặc <a href=\"https://hoyang.vn/chan-vay-chu-a-dang-dai/\">chân váy chữ a VHY20</a> chị em mặc đẹp như nàng công chúa mà không đánh mất đi nét năng động, khỏe khoắn.</p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-kieu-co-beo-form-suong.jpg\" alt=\"áo kiểu cổ bèo form suông\"></p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-kieu-co-beo-tay-dai-1.jpg\" alt=\"áo kiểu cổ bèo form suông phối ren tay dài\"></p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-kieu-co-beo-phoi-ren.jpg\" alt=\"áo kiểu cổ bèo form suông phối ren\"></p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-kieu-co-beo.jpg\" alt=\"áo kiểu cổ bèo form suông phối ren tay dài dễ thương\"></p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-kieu-co-beo-tay-dai.jpg\" alt=\"áo kiểu cổ bèo tay dài\"></p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-banh-beo-tay-bo-chun.jpg\" alt=\"áo bánh bèo tay bo chun\"></p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-kieu-dai-tay-co-beo-tieu-thu.jpg\" alt=\"áo kiểu dài tay cổ bèo tiểu thư\"></p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-so-mi-nu-tieu-thu-tay-dai-nhun-beo.jpg\" alt=\"áo sơ mi tiểu thư tay dài nhún bèo\"></p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-so-mi-co-beo-tay-phong-bo-chun.jpg\" alt=\"áo sơ mi cổ bèo tay phồng bo chun\"></p>\r\n<p dir=\"ltr\" style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Tay Bèo Tiểu Thư Cổ Điển', '', '', '', 'áo-sơ-mi-tay-bèo-tiểu-thư-cổ-điển'),
(15, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Trang phục sơ mi được may bằng chất liệu linen mềm mại, vừa tạo cảm giác thoải mái vừa mang đến vẻ đẹp duyên dáng và thanh lịch. Dù là cô nàng tuổi 20 hay những quý cô trung tuổi đều sẽ trở nên vô cùng cuốn hút và nổi bật khi diện áo sơ mi cao cấp này.</p>\r\n<p>Trong ảnh, mẫu phối áo cùng <a href=\"https://hoyang.vn/chan-vay-body-dai-xe-ta/\">chân váy bút chì ôm body VHY04</a>, <a href=\"https://hoyang.vn/quan-linen-ong-suong/\">quần QHY02.</a></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/07/ao-nu-mau-vang-dep.jpg\" alt=\"áo nữ màu vàng đẹp\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/07/ao-so-mi-nu-mau-vang.jpg\" alt=\"áo sơ mi nữ màu vàng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/07/ao-so-mi-nu-di-lam-mau-vang.jpg\" alt=\"áo sơ mi nữ đi làm màu vàng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/07/ao-so-mi-trang.jpg\" alt=\"ao so mi trang\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/07/ao-so-mi-tay-dai-co-nhon.jpg\" alt=\"ao so mi tay dai co nhon\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/07/ao-so-mi-tay-dai.jpg\" alt=\"ao so mi tay dai\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/07/ao-so-mi-cong-so-tay-dai.jpg\" alt=\"ao so mi cong so tay dai\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Linen Cách Điệu Trẻ Trung', '', '', '', 'áo-sơ-mi-linen-cách-điệu-trẻ-trung'),
(16, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo khoác sơ mi nữ form rộng được nhiều chị em yêu thích nhờ phong cách năng động, trẻ trung và thời thượng. Áo có thiết kế viền nổi, kết hợp màu xanh blue nổi bật, khẳng định chất riêng của nàng giữa đám đông.</p>\r\n<p>Nàng có thể phối áo sơ mi form rộng với <a href=\"https://hoyang.vn/quan-ong-rong-culottes-hack-dang/\">quần tây QUAN001</a> hay <a href=\"https://hoyang.vn/ao-2-day-ban-keo-khoa/\">áo croptop AO001</a> như mẫu đang mặc trong hình để có một set đồ tone sur tone.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-xanh-blue.jpg\" alt=\"áo khoác nữ xanh blue\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/set-quan-ao-mau-xanh.jpg\" alt=\"set quần áo màu xanh\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-so-mi-ca-tinh.jpg\" alt=\"áo sơ mi cá tính\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/thoi-trang-ca-tinh.jpg\" alt=\"thời trang cá tính\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Khoác Sơ Mi Nữ Form Rộng', '', '', '', 'áo-khoác-sơ-mi-nữ-form-rộng'),
(17, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo sơ mi nữ kiểu không tay làm từ chất liệu linen mềm sẽ là lựa chọn tuyệt vời cho mùa hè này. Chất liệu linen mềm giúp cơ thể thoát hơi nhanh chóng, mang lại cảm giác mát mẻ và thoải mái trong những ngày oi bức.</p>\r\n<p>Bạn có thể phối áo sơ mi với quần jeans, chân váy hoặc quần shorts để tạo nên những trang phục cá tính và phong cách. Trong ảnh, mẫu phối áo cùng <a href=\"https://hoyang.vn/chan-vay-xep-ly-dang-dai/\">chân váy dài đen VHY22</a> và <a href=\"https://hoyang.vn/chan-vay-hoa-dang-dai-sieu-dep/\">chân váy VHY06</a>.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/05/ao-so-mi-kieu-co-tui.jpg\" alt=\"áo sơ mi kiểu\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/05/ao-so-mi-kieu-trang.jpg\" alt=\"áo sơ mi kiểu ngắn tay trắng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/05/ao-so-mi-kieu-ngan-tay.jpg\" alt=\"áo sơ mi kiểu ngắn tay\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/05/ao-so-mi-kieu.jpg\" alt=\"áo sơ mi kiểu ngắn tay trắng linen\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/03/chan-vay-va-ao-so-mi.jpg\" alt=\"chan vay va ao so mi\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/03/chan-vay-hoa.jpg\" alt=\"chan vay hoa\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/03/chan-vay-hoa-nhi-dai.jpg\" alt=\"chan vay hoa nhi dai\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Cổ Vest Không Tay Siêu Đỉnh', '', '', '', 'áo-sơ-mi-cổ-vest-không-tay-siêu-đỉnh'),
(18, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Chất liệu ren nữ tính sẽ làm “mềm hóa” dáng áo sơ mi truyền thống, điểm nhấn ren trên áo thu hút người đối diện và đem lại niềm hứng khởi cho bạn gái khởi đầu ngày mới. Trong ảnh, mẫu phối áo cùng <a href=\"https://hoyang.vn/chan-vay-chu-a-ngan/\">chân váy V34</a>.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-so-mi-tay-dai-dep.jpg\" alt=\"áo sơ mi tay dài đẹp\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/trang-phuc-di-lam.jpg\" alt=\"trang phục đi làm\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/chan-vay-cong-so.jpg\" alt=\"chân váy công sở\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-so-mi-va-chan-vay.jpg\" alt=\"áo sơ mi và chân váy\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-so-mi-tay-dai-va-chan-vay.jpg\" alt=\"áo sơ mi tay dài và chân váy\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-so-mi-nu-vat-bau.jpg\" alt=\"áo sơ mi nữ vạt bầu\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Vạt Bầu Phối Ren', '', '', '', 'áo-sơ-mi-vạt-bầu-phối-ren'),
(19, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Thiết kế áo sơ mi khác biệt hoàn toàn so với các kiểu áo sơ mi truyền thống với hàng cúc chạy dài theo đường chéo, kết hợp xẻ tà eo góp phần khẳng định cái tôi và nét độc đáo của người mặc.</p>\r\n<p>Trong ảnh, mẫu phối áo cùng <a href=\"https://hoyang.vn/quan-short-nu-gia-vay/\">quần short giả váy QSHY05</a> và <a href=\"https://hoyang.vn/quan-short-cap-cao/\">quần short cạp cao QSHY01</a>.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-so-mi-chay-cuc-cheo.jpg\" alt=\"áo sơ mi chạy cúc chéo\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-si-mu-nu-kieu-dep.jpg\" alt=\"ao si mu nu kieu dep\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-so-mi-kieu.jpg\" alt=\"áo sơ mi kiểu\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-so-mi-nu-kieu.jpg\" alt=\"áo sơ mi nữ kiểu\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-so-mi-vat-cheo-1.jpg\" alt=\"ao so mi vat cheo 1\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-si-mi-xanh.jpg\" alt=\"áo sơ mi xanh\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Xẻ Tà Eo Phá Cách', '', '', '', 'áo-sơ-mi-xẻ-tà-eo-phá-cách'),
(20, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo sơ mi trắng với phần cổ được thêu tinh xảo tựa như đôi cánh thiên thần ở 2 mặt trước – sau. Đặc biệt áo dài tay kín cổ, thuận tiện phối cùng chân váy hoặc quần tây cho set đồ công sở. Trong ảnh, mẫu phối áo sơ mi cùng chân váy dài VHY22 và <a href=\"https://hoyang.vn/quan-au-nu-ke-caro/\">quần tây caro QSCR</a>.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-so-mi-kieu-phoi-ren.jpg\" alt=\"áo sơ mi kiểu cổ bèo phối ren\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-so-mi-kieu-tieu-thu.jpg\" alt=\"áo sơ mi kiểu cổ bèo phối ren tiểu thư thanh lịch\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-so-mi-kieu-tay-dai.jpg\" alt=\"áo sơ mi kiểu cổ bèo phối ren tiểu thư\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-so-mi-kieu-co-beo.jpg\" alt=\"áo sơ mi kiểu cổ bèo\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-tay-dai-phoi-ren.jpg\" alt=\"áo tay dài phối ren\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-trang-phoi-ren.jpg\" alt=\"áo trắng phối ren\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-tay-dai-mau-trang-2.jpg\" alt=\"áo tay dài màu trắng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-kieu-mau-trang-dep.jpg\" alt=\"áo kiểu màu trắng đẹp\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Ren Đôi Cánh Thiên Thần', '', '', '', 'áo-sơ-mi-ren-đôi-cánh-thiên-thần'),
(21, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo sơ mi tay lỡ với kiểu cổ kín đáo mang lại nét tươi mới cho trang phục công sở của bạn. Tay áo vừa chạm khửu tay, bo chun gọn gàng và cân đối. Bạn có thể mix áo cùng quần tây, jean, chân váy hay <a href=\"https://hoyang.vn/quan-ong-rong-phi-lua/\">quần lụa QHY17</a> như mẫu đang mặc thì cũng rất ăn ý.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-so-mi-tay-lo-dep.jpg\" alt=\"ao so mi tay lo dep\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-tay-lo-in-hoa-tiet-dep.jpg\" alt=\"ao tay lo in hoa tiet dep\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-so-mi-dep.jpg\" alt=\"ao so mi dep\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-so-mi-tay-lo.jpg\" alt=\"ao so mi tay lo\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/hoa-tiet-ngua-van-dep.jpg\" alt=\"hoa tiet ngua van dep\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/06/ao-so-mi-tay-lo-in-hoa-tiet-ngua-van.jpg\" alt=\"ao so mi tay lo in hoa tiet ngua van\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Sơ Mi Lụa Họa Tiết Da Beo', '', '', '', 'áo-sơ-mi-lụa-họa-tiết-da-beo'),
(22, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Chất liệu mềm mại, thiết kế trẻ trung, sành điệu chắc chắn sẽ là item không thể thiếu trong tủ đồ của bạn, Áo Len Croptop Cộc Tay Phối Nút là sự kết hợp hoàn hảo giữa phong cách thời trang hiện đại và sự thoải mái tối đa. Với thiết kế croptop ngắn gọn, phóng khoáng, chiếc áo này dễ dàng mix &amp; match cùng nhiều trang phục khác để tạo nên những set đồ ấn tượng.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/11/a62fc17d8f9b34c56d8a6.jpg\" alt=\"a62fc17d8f9b34c56d8a6\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/11/763901953973822ddb628.jpg\" alt=\"763901953973822ddb628\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/11/22061a5554b3efedb6a22.jpg\" alt=\"22061a5554b3efedb6a22\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/11/0843e713a9f512ab4be43.jpg\" alt=\"0843e713a9f512ab4be43\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/11/711ea64ee8a853f60ab95.jpg\" alt=\"711ea64ee8a853f60ab95\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/11/75bc4b1073f6c8a891e77.jpg\" alt=\"75bc4b1073f6c8a891e77\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/11/49f4a2a4ec42571c0e531.jpg\" alt=\"49f4a2a4ec42571c0e531\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2024/11/8bfdaeade04b5b15025a4.jpg\" alt=\"8bfdaeade04b5b15025a4\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Croptop phối nút siêu sành điệu', '', '', '', 'áo-croptop-phối-nút-siêu-sành-điệu'),
(23, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Nếu bạn đã quá nhàm chán với những chiếc áo len dày cộm, khó phối thì hãy thử qua áo len cộc tay với chất liệu len lông mịn, mẫu áo mới độc lạ, đang được giới sành mặc yêu thích.</p>\r\n<p>Với chất liệu len lông mịn, kiểu dáng thời trang, nàng có thể tự tin phối áo cùng <a href=\"https://hoyang.vn/short-gia-vay-chu-a-sanh-dieu/\">quần short phối nút QS26</a>, <a href=\"https://hoyang.vn/chan-vay-chu-a-ngan/\">chân váy chữ A V34</a> hoặc <a href=\"https://hoyang.vn/chan-vay-ngan-vat-cheo/\">quần giả váy VHY12</a> như mẫu đang mặc, giúp nàng trở thành cô gái sành mặc và ghi điểm sành điệu ngay tức khắc.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-len-co-tim.jpg\" alt=\"áo len cổ tim dễ thương hồng tay ngắn form ôm\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-len-co-tim-hong.jpg\" alt=\"áo len cổ tim dễ thương hồng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-len-co-tim-tay-ngan.jpg\" alt=\"áo len cổ tim dễ thương hồng tay ngắn\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-len-co-tim-de-thuong.jpg\" alt=\"áo len cổ tim dễ thương\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-len-co-tim.jpg\" alt=\"áo len cổ tim nâu tay ngắn điệu đà\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-len-co-tim-tay-ngan.jpg\" alt=\"áo len cổ tim nâu tay ngắn\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-len-co-tim-nau.jpg\" alt=\"áo len cổ tim nâu\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2022/12/ao-len-co-tim-form-om.jpg\" alt=\"áo len cổ tim\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/10/ao-len-mau.jpg\" alt=\"áo len màu dễ thương cổ tim tay ngắn lông\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/10/ao-len-mau-tay-ngan.jpg\" alt=\"áo len màu dễ thương cổ tim tay ngắn\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/10/ao-len-mau-co-tim.jpg\" alt=\"áo len màu dễ thương cổ tim\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/10/ao-len-mau-body.jpg\" alt=\"áo len màu dễ thương\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Len Croptop Cộc Tay Xinh Như Gái Hàn', '', '', '', 'áo-len-croptop-cộc-tay-xinh-như-gái-hàn'),
(24, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo croptop cổ vest là item mang vẻ đẹp hiện đại và phóng khoáng. Đây là món đồ thời trang được được yêu thích nhất tại trời Tây, liên tục được các cô gái diện không chỉ nơi công sở mà còn trong các bữa tiệc hay khi họ đi dạo phố.</p>\r\n<p>Trong ảnh mẫu đang phối áo cùng <a href=\"https://hoyang.vn/quan-tay-phoi-day-dan-tui/\">quần tây ống suông QHY75</a> và <a href=\"https://hoyang.vn/ao-thun-trang-tron/\">áo thun croptop AHY76</a>. Mua nguyên <a href=\"https://hoyang.vn/set-bo-vest/\">set SBAHY75 3 món</a>, giá giảm 10%.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-kieu-co-vest-cool-ngau.jpg\" alt=\"áo kiểu cổ vest cool ngầu\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-kieu-so-mi-croptop.jpg\" alt=\"áo kiểu sơ mi croptop\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-kieu-so-mi-de-thuong.jpg\" alt=\"áo kiểu sơ mi dễ thương\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-kieu-so-mi-tay-ngan.jpg\" alt=\"áo kiểu sơ mi tay ngắn\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Croptop Cổ Vest Phong Cách Hiện Đại', '', '', '', 'áo-croptop-cổ-vest-phong-cách-hiện-đại'),
(25, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo thiết kế cổ tròn, đính thêm cút tròn ngay eo siêu sexy, chỉ cần phối cùng một chiếc quần jeans ôm hoặc <a href=\"https://hoyang.vn/quan-short-lai-luon-song/\">quần short ống rộng QS73</a> như mẫu đang mặc là đã chất hết nấc rồi. Áo sử dụng chất liệu thun Hàn Quốc cao cấp, co giãn thoải mái, mặc lên rất tôn dáng.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/quan-sort-lai-luon-song.jpg\" alt=\"quần sort lật lai lượn sóng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-thun-tay-dai-phoi-nut.jpg\" alt=\"áo thun tay dài phối nút\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-thun-tay-dai-co-tron.jpg\" alt=\"áo thun tay dài phối nút\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-thun-tay-dai.jpg\" alt=\"áo thun tay dài\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/09/ao-thun-tay-dai-dao-cheo.jpg\" alt=\"áo thun tay dài tà chéo\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Thun Croptop Tay Dài Cổ Tròn', '', '', '', 'áo-thun-croptop-tay-dài-cổ-tròn'),
(26, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo croptop 2 màu cực trendy phối cùng <a href=\"https://hoyang.vn/quan-dai-2-mau/\">quần ống suông QHY48</a> sẽ là bộ cánh hoàn hảo tôn thêm phong cách cool ngầu, cá tính sẵn có trong con người của nàng đấy. Thử ngay bộ đôi hoàn hảo này trong buổi đi chơi sắp tới nàng nha.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-cheo-vai-2-mau.jpg\" alt=\"áo chéo vai 2 màu\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-cheo-vai-2-mau-phoi-ren.jpg\" alt=\"áo chéo vai 2 màu croptop phối ren\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-cheo-vai-2-mau-xanh-vang.jpg\" alt=\"áo chéo vai 2 màu croptop phối ren xanh vàng\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-cheo-vai-2-mau-croptop.jpg\" alt=\"áo chéo vai 2 màu croptop\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo CropTop Lệch Vai Phối 2 Màu Độc Lạ', '', '', '', 'áo-croptop-lệch-vai-phối-2-màu-độc-lạ'),
(27, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Với thiết kế tay cánh dơi một bên độc đáo cùng chi tiết cổ vest thời thượng, áo croptop HoYang mang đến phong cách cool ngầu, trẻ trung đầy khí chất.</p>\r\n<p>Có nhiều cách để phối áo croptop HoYang chẳng hạn như phối cùng <a href=\"https://hoyang.vn/quan-tay-ong-dung/\">quần tây ống đứng QHY58</a> mẫu đang mặc thì cũng đều rất mới mẻ và đặc sắc.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-ho-1-vai-tay-canh-doi.jpg\" alt=\"áo hở 1 vai tay cánh dơi\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-croptop-lech-vai-1-tay.jpg\" alt=\"áo croptop lệch vai 1 tay\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-croptop-co-vest-soc-den.jpg\" alt=\"áo croptop cổ vest sọc đen\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/ao-coptop-lech-vai-1-tay-ca-tinh.jpg\" alt=\"áo croptop lệch vai 1 tay cá tính\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/set-bo-co-vest-cool-ngau.jpg\" alt=\"áo cổ vest sọc đen\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/set-bo-co-vest-cool-ngau-den-quyen-quy.jpg\" alt=\"sét bộ vest cool ngầu\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/set-bo-co-vest-cool-ngau-ta-cheo.jpg\" alt=\"áo cổ vest siêu sang\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/08/set-bo-co-vest-cool-ngau-soc-den.jpg\" alt=\"áo sơ mi cổ vest cá tính\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Croptop Cổ Vest Chất Kaki Hàn', '', '', '', 'áo-croptop-cổ-vest-chất-kaki-hàn'),
(28, '', '<h2>Thông tin sản phẩm</h2>\r\n<p>Áo croptop form ôm có khả năng biến hoá cực ngọt nên phù hợp với nhiều hoàn cảnh và nhiều phong cách ăn mặc. Phần eo ôm tạo điểm nhấn giúp nàng khoe vòng 2 thon gọn, toát lên nét đẹp quyến rũ, gợi cảm khó cưỡng.</p>\r\n<p>Để có một set đồ hoàn hảo nhất, các nàng hãy ưu tiên mix áo với chân váy, quần kiểu dáng basic để kết hợp thật ăn ý với nhau. Trong ảnh, mẫu phối áo cùng <a href=\"https://hoyang.vn/chan-vay-den-co-tui/\">chân váy chữ A ngắn VHY17</a>, <a href=\"https://hoyang.vn/chan-vay-hoa-dang-xoe/\">chân váy xòe VHY40</a>.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-lua-no-vai-hoa-do.jpg\" alt=\"áo lụa nơ vai hoa đỏ\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-lua-co-vuong-croptop.jpg\" alt=\"áo lụa cổ vuông croptop\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-lua-no-vai.jpg\" alt=\"áo lụa nơ vai\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-lua-hoa-do.jpg\" alt=\"áo lụa hoa đỏ\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/set-bo-ao-croptop-chan-vay-hoa-dai.jpg\" alt=\"chân váy lụa in hoa\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/set-bo-hoa-no-vai.jpg\" alt=\"set bộ hoa nơ vai\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/set-bo-hoa-la.jpg\" alt=\"sét bộ lụa in hoa\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/set-bo-hoa-phuong.jpg\" alt=\"sét bộ hoa phương\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/ao-croptop-hoa-va-chan-vay-hoa.jpg\" alt=\"áo croptop hoa và chân váy hoa\"></p>\r\n<p style=\"text-align: center;\"><img src=\"https://hoyang.vn/wp-content/uploads/2023/06/set-ao-croptop-va-chan-vay.jpg\" alt=\"set áo croptop và chân váy\"></p>\r\n<p style=\"text-align: center;\"> </p>', 'Áo Croptop Hoa Form Ôm Điệu Đà', '', '', '', 'áo-croptop-hoa-form-ôm-điệu-đà');
